[TextBlock(citations=None, text="```typescript\n// types/index.d.ts\n\n// Navigation Types\nexport interface NavigationItem {\n  title: string;\n  path: string;\n  isButton?: boolean;\n}\n\n// Hero Types\nexport interface HeroSection {\n  heading: string;\n  subheading: string;\n  backgroundImage: string;\n  ctaText: string;\n  ctaLink: string;\n  videoUrl?: string;\n}\n\n// Menu Types\nexport interface MenuItem {\n  id: string;\n  name: string;\n  description: string;\n  price: string;\n  image?: string;\n  isSignature?: boolean;\n  region?: string;\n  allergens?: string[];\n  dietaryInfo?: string[];\n}\n\nexport interface MenuCategory {\n  id: string;\n  name: string;\n  description?: string;\n  items: MenuItem[];\n}\n\nexport interface MenuRegion {\n  id: string;\n  name: string;\n  description: string;\n  position: {\n    x: number;\n    y: number;\n  };\n  image?: string;\n}\n\n// Testimonial Types\nexport interface Testimonial {\n  id: string;\n  author: string;\n  rating: number;\n  content: string;\n  date: string;\n  image?: string;\n}\n\n// About Types\nexport interface TeamMember {\n  id: string;\n  name: string;\n  role: string;\n  bio: string;\n  image: string;\n}\n\nexport interface TimelineEvent {\n  id: string;\n  year: string;\n  title: string;\n  description: string;\n  image?: string;\n}\n\n// Gallery Types\nexport interface GalleryImage {\n  id: string;\n  src: string;\n  alt: string;\n  category: 'food' | 'restaurant' | 'events' | 'chef';\n  width: number;\n  height: number;\n}\n\n// Reservation Types\nexport interface TimeSlot {\n  id: string;\n  time: string;\n  available: boolean;\n}\n\nexport interface ReservationRequest {\n  name: string;\n  email: string;\n  phone: string;\n  date: string;\n  time: string;\n  guests: number;\n  seatingPreference?: 'indoor' | 'outdoor' | 'bar';\n  specialRequests?: string;\n}\n\nexport interface AvailabilityResponse {\n  date: string;\n  timeSlots: TimeSlot[];\n}\n\n// Contact Types\nexport interface ContactInfo {\n  address: string;\n  phone: string;\n  email: string;\n  hours: {\n    [key: string]: string;\n  };\n  socialMedia: {\n    facebook?: string;\n    instagram?: string;\n    twitter?: string;\n    yelp?: string;\n  };\n}\n\nexport interface ContactFormData {\n  name: string;\n  email: string;\n  subject: string;\n  message: string;\n}\n\n// SEO Types\nexport interface SEOData {\n  title: string;\n  description: string;\n  keywords: string[];\n  ogImage?: string;\n  canonical?: string;\n}\n\n// Theme Types\nexport interface ThemeColors {\n  primary: string;\n  secondary: string;\n  accent: string;\n  background: string;\n  text: string;\n  textLight: string;\n}\n\n// Analytics Types\nexport interface AnalyticsEvent {\n  category: string;\n  action: string;\n  label?: string;\n  value?: number;\n}\n```", type='text')]